#Combien d'handle contiennent un numéro ?
def how_many_number
  handle_with_number = []

   $journalists.each do |handle|  # attention Suzy du futur ce code est dégueu, mais c'est la seule manière vrt autonome que j'aie trouvé...
     if handle.include? "0" 
      handle_with_number << handle
     elsif handle.include? "1" 
      handle_with_number << handle
     elsif handle.include? "2" 
      handle_with_number << handle
    elsif handle.include? "3" 
      handle_with_number << handle
    elsif handle.include? "4" 
      handle_with_number << handle
    elsif handle.include? "5" 
      handle_with_number << handle
    elsif handle.include? "6" 
      handle_with_number << handle
    elsif handle.include? "7" 
      handle_with_number << handle
    elsif handle.include? "8" 
      handle_with_number << handle
    elsif handle.include? "9" 
      handle_with_number << handle
     end
    end
  
    puts handle_with_number.length
    
end

#Combien d'handle contiennent les 4 lettres du prénom "Aude" à la suite (sans prendre en compte les majuscules) ? 
def aude
  handle_with_letter_aude = []

  $journalists.each do |handle|
    if handle.include? "aude" 
      handle_with_letter_aude << handle
    end
  end

    puts handle_with_letter_aude.count
end

# Combien commencent par une majuscule (première lettre juste après le @) ?

def capitalize_letter

  handle_with_first_letter_array = []

# Pour accéder à la première lettre des mails on enlève @ et on les stocke dans un array sur lequel on pourra itérer

  $journalists.each do |handle|
    handle.gsub!(/[@]/, "")
    handle_with_first_letter_array << handle
  end
    #print handle_with_first_letter_array
  
  handle_with_first_letter_array.each do |mail| # après avoir enlevé @ on peut appliquer l'autre méthode que nous avons crée
    is_capitalize?(mail)
    end
end

# On crée une méthode pour déterminer si la 1ère lettre du mail sans @ est une majuscule

def is_capitalize?(mail)

  first_capitalize_letter_array = [] # array de stockage final
  char_array = [] 
    mail.each_char do |c| # itération qui permet de créer un array avec chaque lettres d'une string
      char_array.push(c)
        if char_array.first == c.capitalize 
          first_capitalize_letter_array << mail
        end
    end
  #print first_capitalize_letter_array.uniq!
end

