def uppercase_handle(journalist)
  puts "There are #{journalist.select{ |u| /@[A-Z]/.match(u)}.length} handles which start with uppercase letter"
  puts
end

# Combien contiennent au moins une majuscule ?

def uppercase_handle_bis(journalist)
  puts "There are #{journalist.select{ |i| /[A-Z]/.match(i)}.length} handles with an uppercase letter"
  puts
end

# Combien y a-t-il de underscore _ dans tous les pseudos confondus ?

def underscore_number(journalist)
  puts "There are #{journalist.count{ |underscore| /_/.match(underscore)}} underscore in the list"
  puts
end

